  app.post("/api/auth/login", async (req: SessionRequest, res) => {
    try {
      const { username, password } = req.body;

      // First check database users (case-insensitive username lookup)
      const user = await storage.getUserByUsername(username);

      if (user && user.password) {
        let isPasswordValid = false;

        // Check if password is hashed (bcrypt) or plain text (legacy)
        if (user.password.startsWith('$2b$') || user.password.startsWith('$2a$')) {
          // Hashed password - use bcrypt
          isPasswordValid = await bcrypt.compare(password, user.password);
        } else {
          // Plain text password (legacy) - direct comparison
          isPasswordValid = user.password === password;

          // Flag user for password reset if using legacy plain text password
          if (isPasswordValid) {
            await storage.flagUserForPasswordReset(user.id);
          }
        }

        if (isPasswordValid) {
          if (user.isSuspended) {
            return res.status(403).json({ message: "Account suspended" });
          }

          if (req.session) {
            req.session.userId = user.id;
          }
          const { password: _, ...userWithoutPassword } = user;

          // Check if user needs to reset password
          if (user.requirePasswordReset || (!user.password.startsWith('$2b$') && !user.password.startsWith('$2a$'))) {
            return res.json({
              ...userWithoutPassword,
              requirePasswordReset: true,
              message: "Your password must be updated to meet new security requirements"
            });
          }

          // Check if user needs to change username
          if (user.requireUsernameChange) {
            // Ensure session is set even for username change flow
            req.session.userId = user.id;

            return res.json({
              ...userWithoutPassword,
              requireUsernameChange: true,
              message: "Your username has been flagged for change. Please choose a new username."
            });
          }

          return res.json(userWithoutPassword);
        }
      }

      // Fallback: Check for admin credentials (case-insensitive)
      if (username.toLowerCase() === "jkimber" && password === "admin123") {
        const adminUser = {
          id: "jkimber-admin",
          username: "jkimber",
          email: "jkimber@cagunexchange.com",
          role: "admin",
          isAdmin: true,
          isActive: true,
          createdAt: new Date()
        };
        return res.json(adminUser);
      }

      return res.status(401).json({ message: "Invalid credentials" });
    } catch (error) {
      console.error("Login error:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: error.errors[0].message });
      }
      res.status(500).json({ message: "Login failed" });
    }
  });

