    try {
      // Use the working method that includes author data
      const allPostsWithAuthors = await storage.getPostsWithAuthors();
      const categoryPosts = allPostsWithAuthors
        .filter(post => post.categoryId === req.params.categoryId)
        .sort((a, b) => {
          // First sort by pinned status (pinned posts first)
          if (a.isPinned && !b.isPinned) return -1;
          if (!a.isPinned && b.isPinned) return 1;

          // Then sort by bump/creation date
          return (b.bumpedAt?.getTime() || b.createdAt?.getTime() || 0) - (a.bumpedAt?.getTime() || a.createdAt?.getTime() || 0);
        });

      res.json(categoryPosts);
    } catch (error) {
