        {
          owner: userId,
          visibility: "public", // Profile pictures should be publicly visible
        }
      );

      // Update user's profile picture in database
      const updatedUser = await storage.updateUserProfile(userId, { profilePicture: objectPath });
      if (!updatedUser) {
        return res.status(404).json({ message: "User not found" });
      }

      const { password, ...userWithoutPassword } = updatedUser;
      res.json({ user: userWithoutPassword, objectPath });
    } catch (error) {
      console.error("Error setting profile picture:", error);
      res.status(500).json({ message: "Failed to set profile picture" });
    }
  });

  // Object storage routes
  app.post("/api/objects/upload", async (req, res) => {
    try {
      const objectStorageService = new ObjectStorageService();
      const uploadURL = await objectStorageService.getPublicObjectUploadURL();
      res.json({ uploadURL });
    } catch (error) {
      console.error("Error getting upload URL:", error);
      res.status(500).json({ error: "Failed to get upload URL" });
    }
  });

  // Public user profile routes
  app.get("/api/users/:id", async (req, res) => {
    try {
      const userId = req.params.id;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Return public user data (excluding sensitive info)
      const publicUser = {
        id: user.id,
        username: user.username,
        firstName: user.firstName,
        lastName: user.lastName,
        location: user.location,
        bio: user.bio,
        profilePicture: user.profilePicture,
        isVerified: user.isVerified,
        createdAt: user.createdAt,
      };

      res.json(publicUser);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  app.get("/api/users/:id/posts", async (req, res) => {
    try {
      const userId = req.params.id;

      // Verify user exists
      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      const posts = await storage.getPostsByUser(userId);
      res.json(posts);
    } catch (error) {
      console.error("Error fetching user posts:", error);
      res.status(500).json({ message: "Failed to fetch user posts" });
    }
  });

  // Search API
  app.get("/api/search", async (req, res) => {
    try {
      const query = req.query.q as string;
      if (!query || !query.trim()) {
        return res.status(400).json({ message: "Search query is required" });
      }

      const [postResults, userResults] = await Promise.all([
        storage.searchPosts(query.trim()),
        storage.searchUsers(query.trim())
      ]);

      res.json({
        posts: postResults,
        users: userResults,
        totalResults: postResults.length + userResults.length
      });
    } catch (error) {
      console.error("Error performing search:", error);
      res.status(500).json({ message: "Search failed" });
    }
  });

  // User logout route
  app.post('/api/logout', (req, res) => {
    res.json({ message: "Logged out successfully" });
  });

  // Moderator routes for main site (require admin or moderator privileges)
  app.delete("/api/posts/:id/moderate", async (req: SessionRequest, res) => {
    try {
      const userId = req.session?.userId;
      if (!userId) {
        return res.status(401).json({ message: "Not authenticated" });
      }

      const user = await storage.getUser(userId);
      if (!user || (!user.isAdmin && !user.isModerator)) {
        return res.status(403).json({ message: "Insufficient privileges" });
      }

      const deleted = await storage.deletePost(req.params.id);
      if (!deleted) {
        return res.status(404).json({ message: "Post not found" });
      }

      res.json({ message: "Post deleted successfully" });
    } catch (error) {
      console.error('Error deleting post:', error);
      res.status(500).json({ message: "Failed to delete post" });
    }
  });

  app.post("/api/posts/:id/pin", async (req: SessionRequest, res) => {
    try {
      const userId = req.session?.userId;
      if (!userId) {
        return res.status(401).json({ message: "Not authenticated" });
      }

      const user = await storage.getUser(userId);
      if (!user || (!user.isAdmin && !user.isModerator)) {
        return res.status(403).json({ message: "Insufficient privileges" });
      }

      const post = await storage.togglePinPost(req.params.id);
      if (!post) {
        return res.status(404).json({ message: "Post not found" });
      }

      res.json(post);
    } catch (error) {
      console.error('Error pinning/unpinning post:', error);
      res.status(500).json({ message: "Failed to pin/unpin post" });
    }
  });

  app.post("/api/users/:id/suspend", async (req: SessionRequest, res) => {
    try {
      const userId = req.session?.userId;
      if (!userId) {
        return res.status(401).json({ message: "Not authenticated" });
      }

      const user = await storage.getUser(userId);
      if (!user || (!user.isAdmin && !user.isModerator)) {
        return res.status(403).json({ message: "Insufficient privileges" });
      }

      const targetUser = await storage.toggleUserSuspension(req.params.id);
      if (!targetUser) {
        return res.status(404).json({ message: "User not found" });
      }

      const { password, ...userWithoutPassword } = targetUser;
      res.json(userWithoutPassword);
    } catch (error) {
      console.error('Error suspending/unsuspending user:', error);
      res.status(500).json({ message: "Failed to suspend/unsuspend user" });
    }
  });

  // Moderator routes for user flagging (require admin or moderator privileges)
  app.put("/api/users/:id/flag-username-change", async (req: SessionRequest, res) => {
    try {
      const userId = req.session?.userId;
      if (!userId) {
        return res.status(401).json({ message: "Not authenticated" });
      }

      const user = await storage.getUser(userId);
      if (!user || (!user.isAdmin && !user.isModerator)) {
        return res.status(403).json({ message: "Insufficient privileges" });
      }

      const targetUser = await storage.flagUserForUsernameChange(req.params.id);
      if (!targetUser) {
        return res.status(404).json({ message: "User not found" });
      }

      const { password, ...userWithoutPassword } = targetUser;
      res.json(userWithoutPassword);
    } catch (error) {
      console.error('Error flagging user for username change:', error);
      res.status(500).json({ message: "Failed to flag user for username change" });
    }
  });

  app.put("/api/users/:id/flag-password-reset", async (req: SessionRequest, res) => {
    try {
      const userId = req.session?.userId;
      if (!userId) {
        return res.status(401).json({ message: "Not authenticated" });
      }

      const user = await storage.getUser(userId);
      if (!user || (!user.isAdmin && !user.isModerator)) {
        return res.status(403).json({ message: "Insufficient privileges" });
      }

      const targetUser = await storage.flagUserForPasswordReset(req.params.id);
      if (!targetUser) {
        return res.status(404).json({ message: "User not found" });
      }

      const { password, ...userWithoutPassword } = targetUser;
      res.json(userWithoutPassword);
    } catch (error) {
      console.error('Error flagging user for password reset:', error);
      res.status(500).json({ message: "Failed to flag user for password reset" });
    }
  });

  // Admin routes
  app.get("/api/admin/users", async (req, res) => {
    try {
      const users = await storage.getAllUsers();
      const usersWithoutPasswords = users.map(({ password, ...user }) => user);
      res.json(usersWithoutPasswords);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch users" });
    }
  });

  app.post("/api/admin/users/:id/suspend", async (req, res) => {
    try {
      const user = await storage.toggleUserSuspension(req.params.id);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      const { password, ...userWithoutPassword } = user;
      res.json(userWithoutPassword);
    } catch (error) {
      res.status(500).json({ message: "Failed to suspend/unsuspend user" });
    }
  });

  app.delete("/api/admin/users/:id", async (req, res) => {
    try {
      const deleted = await storage.deleteUser(req.params.id);
      if (!deleted) {
        return res.status(404).json({ message: "User not found" });
      }

      res.json({ message: "User deleted successfully" });
    } catch (error) {
      res.status(500).json({ message: "Failed to delete user" });
    }
  });

  app.delete("/api/admin/posts/:id", async (req, res) => {
    try {
      console.log('Admin deleting post:', req.params.id);
      const deleted = await storage.deletePost(req.params.id);
      if (!deleted) {
        console.log('Post not found for deletion:', req.params.id);
        return res.status(404).json({ message: "Post not found" });
      }

      console.log('Post deleted successfully:', req.params.id);
      res.json({ message: "Post deleted successfully" });
    } catch (error) {
      console.error('Error deleting post:', error);
      res.status(500).json({ message: "Failed to delete post" });
    }
  });

  app.post("/api/admin/users/:id/make-admin", async (req, res) => {
    try {
      const user = await storage.makeUserAdmin(req.params.id);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      const { password, ...userWithoutPassword } = user;
      res.json(userWithoutPassword);
    } catch (error) {
      res.status(500).json({ message: "Failed to make user admin" });
    }
  });

  app.post("/api/admin/users/:id/toggle-moderator", async (req, res) => {
    try {
      const user = await storage.toggleUserModerator(req.params.id);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      const { password, ...userWithoutPassword } = user;
      res.json(userWithoutPassword);
    } catch (error) {
      res.status(500).json({ message: "Failed to toggle moderator status" });
    }
  });

  app.put("/api/admin/users/:id/flag-username-change", async (req, res) => {
    try {
      const user = await storage.flagUserForUsernameChange(req.params.id);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      const { password, ...userWithoutPassword } = user;
      res.json(userWithoutPassword);
    } catch (error) {
      console.error("Error flagging user for username change:", error);
      res.status(500).json({ message: "Failed to flag user for username change" });
    }
  });

  // Route to handle username change for flagged users
  app.put("/api/profile/change-username", async (req: any, res) => {
    try {
      if (!req.session?.userId) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      const { newUsername } = req.body;
      if (!newUsername || newUsername.trim().length === 0) {
        return res.status(400).json({ message: "New username is required" });
      }

      const user = await storage.getUserByUsername(newUsername.trim());
      if (user && user.id !== req.session.userId) {
        return res.status(400).json({ message: "Username already exists" });
      }

      const updatedUser = await storage.updateUserUsername(req.session.userId, newUsername.trim());
      if (!updatedUser) {
        return res.status(404).json({ message: "User not found" });
      }

      // Clear the username change flag
      await storage.clearUsernameChangeFlag(req.session.userId);

      const { password, ...userWithoutPassword } = updatedUser;
      res.json(userWithoutPassword);
    } catch (error) {
      console.error("Error changing username:", error);
      res.status(500).json({ message: "Failed to change username" });
    }
  });

  // Object storage routes
  app.get("/public-objects/:filePath(*)", async (req, res) => {
    const filePath = req.params.filePath;
    const objectStorageService = new ObjectStorageService();
    try {
      const file = await objectStorageService.searchPublicObject(filePath);
      if (!file) {
        return res.status(404).json({ error: "File not found" });
      }
      objectStorageService.downloadObject(file, res);
    } catch (error) {
      console.error("Error searching for public object:", error);
      return res.status(500).json({ error: "Internal server error" });
    }
  });

  app.get("/objects/:objectPath(*)", async (req, res) => {
    const objectStorageService = new ObjectStorageService();
    try {
      const objectFile = await objectStorageService.getObjectEntityFile(
        req.path,
      );
      objectStorageService.downloadObject(objectFile, res);
    } catch (error) {
      console.error("Error checking object access:", error);
      if (error instanceof ObjectNotFoundError) {
        return res.sendStatus(404);
      }
      return res.sendStatus(500);
    }
  });

  app.post("/api/objects/upload", async (req, res) => {
    const objectStorageService = new ObjectStorageService();
    try {
      const uploadURL = await objectStorageService.getPublicObjectUploadURL();
      res.json({ uploadURL });
    } catch (error) {
      console.error("Error getting upload URL:", error);
      res.status(500).json({ error: "Failed to get upload URL" });
    }
  });

  app.put("/api/post-images", async (req, res) => {
    if (!req.body.imageURL) {
      return res.status(400).json({ error: "imageURL is required" });
    }

    try {
      const objectStorageService = new ObjectStorageService();
      const objectPath = objectStorageService.normalizeObjectEntityPath(
        req.body.imageURL,
      );

      res.status(200).json({
        objectPath: objectPath,
      });
    } catch (error) {
      console.error("Error setting post image:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  // Stats route
  app.get("/api/stats", async (req, res) => {
    try {
      const stats = await storage.getStats();
      res.json(stats);
    } catch (error) {
      res.status(500).json({ message: "Failed to fetch stats" });
    }
  });

  // Advertisement routes
  app.get("/api/advertisements/:position?", async (req, res) => {
    try {
      const { position } = req.params;
      const advertisements = await storage.getAdvertisements(position);
      res.json(advertisements);
    } catch (error) {
      console.error("Error fetching advertisements:", error);
      res.status(500).json({ message: "Failed to fetch advertisements" });
    }
  });

  app.post("/api/advertisements", async (req, res) => {
    try {
      // Only allow admin users to create advertisements
      if (!req.body.isAdmin) {
        return res.status(403).json({ message: "Admin access required" });
      }

      const advertisement = await storage.createAdvertisement(req.body);
      res.status(201).json(advertisement);
    } catch (error) {
      console.error("Error creating advertisement:", error);
      res.status(500).json({ message: "Failed to create advertisement" });
    }
  });

  app.put("/api/advertisements/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const advertisement = await storage.updateAdvertisement(id, req.body);

      if (!advertisement) {
        return res.status(404).json({ message: "Advertisement not found" });
      }

      res.json(advertisement);
    } catch (error) {
      console.error("Error updating advertisement:", error);
      res.status(500).json({ message: "Failed to update advertisement" });
    }
  });

  app.delete("/api/advertisements/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const advertisement = await storage.updateAdvertisement(id, { isActive: false });

      if (!advertisement) {
        return res.status(404).json({ message: "Advertisement not found" });
      }

      res.json({ message: "Advertisement deleted successfully" });
    } catch (error) {
      console.error("Error deleting advertisement:", error);
      res.status(500).json({ message: "Failed to delete advertisement" });
    }
  });

  // Featured listing routes
  app.get("/api/featured-listings", async (req, res) => {
    try {
      const featuredListings = await storage.getActiveFeaturedListings();
      res.json(featuredListings);
    } catch (error) {
      console.error("Error fetching featured listings:", error);
      res.status(500).json({ message: "Failed to fetch featured listings" });
    }
  });

  app.post("/api/featured-listings", async (req, res) => {
    try {
      const featuredListing = await storage.createFeaturedListing(req.body);
      res.status(201).json(featuredListing);
    } catch (error) {
      console.error("Error creating featured listing:", error);
      res.status(500).json({ message: "Failed to create featured listing" });
    }
  });

  // Change password route
  app.post("/api/auth/change-password", async (req, res) => {
    try {
      const userId = req.headers["x-user-id"] as string;
      if (!userId) {
        return res.status(401).json({ message: "User not authenticated" });
      }

      const passwordData = changePasswordSchema.parse(req.body);

      const result = await storage.changeUserPassword(
        userId, 
        passwordData.currentPassword, 
        passwordData.newPassword
      );

      if (result.success) {
        // Clear password reset flag after successful change
        await storage.clearPasswordResetFlag(userId);
        res.json({ message: result.message });
      } else {
        res.status(400).json({ message: result.message });
      }
    } catch (error) {
      console.error("Password change error:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: error.errors[0].message });
      }
      res.status(500).json({ message: "Failed to change password" });
    }
  });

  // Admin route to flag user for password reset
  app.post("/api/admin/flag-password-reset/:userId", async (req, res) => {
    try {
      const { userId } = req.params;
      const result = await storage.flagUserForPasswordReset(userId);

      if (result) {
        res.json({ message: "User flagged for password reset successfully" });
      } else {
        res.status(404).json({ message: "User not found" });
      }
    } catch (error) {
      console.error("Error flagging user for password reset:", error);
      res.status(500).json({ message: "Failed to flag user for password reset" });
    }
  });

  // Clear password reset flag
  app.post("/api/admin/clear-password-reset/:userId", async (req, res) => {
    try {
      const { userId } = req.params;
      await storage.clearPasswordResetFlag(userId);
      res.json({ message: "Password reset flag cleared successfully" });
    } catch (error) {
      console.error("Error clearing password reset flag:", error);
      res.status(500).json({ message: "Failed to clear password reset flag" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

