import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, integer, boolean, json } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  email: text("email").notNull().unique(),
  password: text("password").notNull(),
  dateOfBirth: timestamp("date_of_birth").notNull(),
  firstName: text("first_name"),
  lastName: text("last_name"),
  location: text("location"),
  bio: text("bio"),
  profilePicture: text("profile_picture"), // URL to profile picture in object storage
  isVerified: boolean("is_verified").default(false),
  isAdmin: boolean("is_admin").default(false),
  isModerator: boolean("is_moderator").default(false),
  isSuspended: boolean("is_suspended").default(false),
  requirePasswordReset: boolean("require_password_reset").default(false),
  requireUsernameChange: boolean("require_username_change").default(false),
  createdAt: timestamp("created_at").default(sql`now()`),
  updatedAt: timestamp("updated_at").default(sql`now()`),
});

export const categories = pgTable("categories", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  slug: text("slug").notNull().unique(),
  type: text("type").notNull(), // 'wts', 'wtb', 'wtt', 'discussion'
  description: text("description"),
  icon: text("icon"),
});

export const posts = pgTable("posts", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  content: text("content").notNull(),
  authorId: varchar("author_id").notNull(),
  categoryId: varchar("category_id").notNull(),
  price: integer("price"), // in cents, for WTS posts
  location: text("location"),
  contactInfo: text("contact_info"),
  images: json("images").$type<string[]>().default([]), // Array of image URLs (max 5)
  isActive: boolean("is_active").default(true),
  views: integer("views").default(0),
  isPinned: boolean("is_pinned").default(false),
  pinnedAt: timestamp("pinned_at"),
  willingToTravel: boolean("willing_to_travel").default(false),
  willingToShip: boolean("willing_to_ship").default(false),
  willingToTrade: boolean("willing_to_trade").default(false),
  createdAt: timestamp("created_at").default(sql`now()`),
  updatedAt: timestamp("updated_at").default(sql`now()`),
  bumpedAt: timestamp("bumped_at").default(sql`now()`), // Track when post was last bumped for sorting
});

export const replies = pgTable("replies", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  content: text("content").notNull(),
  authorId: varchar("author_id").notNull(),
  postId: varchar("post_id").notNull(),
  createdAt: timestamp("created_at").default(sql`now()`),
});

export const conversations = pgTable("conversations", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  participant1Id: varchar("participant1_id").notNull(),
  participant2Id: varchar("participant2_id").notNull(),
  postId: varchar("post_id"), // Optional: link to the post that started the conversation
  lastMessageAt: timestamp("last_message_at").default(sql`now()`),
  createdAt: timestamp("created_at").default(sql`now()`),
});

export const messages = pgTable("messages", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  conversationId: varchar("conversation_id").notNull(),
  senderId: varchar("sender_id").notNull(),
  content: text("content").notNull(),
  isRead: boolean("is_read").default(false),
  createdAt: timestamp("created_at").default(sql`now()`),
});

export const advertisements = pgTable("advertisements", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  title: text("title").notNull(),
  description: text("description").notNull(),
  imageUrl: text("image_url"),
  targetUrl: text("target_url").notNull(),
  sponsor: text("sponsor").notNull(),
  sponsorEmail: text("sponsor_email").notNull(),
  position: text("position").notNull(), // 'header', 'sidebar', 'footer', 'in-feed'
  size: text("size").default("medium"), // 'small', 'medium', 'large'
  isActive: boolean("is_active").default(true),
  impressions: integer("impressions").default(0),
  clicks: integer("clicks").default(0),
  startDate: timestamp("start_date").default(sql`now()`),
  endDate: timestamp("end_date"),
  monthlyRate: integer("monthly_rate"), // in cents
  createdAt: timestamp("created_at").default(sql`now()`),
  updatedAt: timestamp("updated_at").default(sql`now()`),
});

export const featuredListings = pgTable("featured_listings", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  postId: varchar("post_id").notNull(),
  sponsorId: varchar("sponsor_id").notNull(),
  featuredUntil: timestamp("featured_until").notNull(),
  dailyRate: integer("daily_rate").notNull(), // in cents
  isActive: boolean("is_active").default(true),
  createdAt: timestamp("created_at").default(sql`now()`),
});

export const userPreferences = pgTable("user_preferences", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull().unique(),
  emailNotifications: boolean("email_notifications").default(true),
  messageNotifications: boolean("message_notifications").default(true),
  marketingEmails: boolean("marketing_emails").default(false),
  profileVisibility: varchar("profile_visibility").default("public"), // "public", "registered", "private"
  showEmail: boolean("show_email").default(false),
  showLocation: boolean("show_location").default(true),
  theme: varchar("theme").default("system"), // "light", "dark", "system"
  createdAt: timestamp("created_at").default(sql`now()`),
  updatedAt: timestamp("updated_at").default(sql`now()`),
});

// Password history table to track last 4 passwords
export const passwordHistory = pgTable("password_history", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  userId: varchar("user_id").notNull(),
  passwordHash: text("password_hash").notNull(),
  createdAt: timestamp("created_at").default(sql`now()`),
});

// Password history types
export type PasswordHistory = typeof passwordHistory.$inferSelect;
export type InsertPasswordHistory = typeof passwordHistory.$inferInsert;

// Password validation function
const passwordValidation = z.string()
  .min(10, "Password must be at least 10 characters long")
  .regex(/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]/, 
    "Password must contain at least one lowercase letter, one uppercase letter, one number, and one special character (@$!%*?&)");

export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
  createdAt: true,
  isVerified: true,
  isAdmin: true,
  isSuspended: true,
}).extend({
  username: z.string()
    .min(3, "Username must be at least 3 characters long")
    .max(20, "Username cannot exceed 20 characters")
    .regex(/^[a-zA-Z0-9_-]+$/, "Username can only contain letters, numbers, underscores, and hyphens (no spaces)"),
  firstName: z.string().min(1, "First name is required").max(50, "First name cannot exceed 50 characters"),
  lastName: z.string().min(1, "Last name is required").max(50, "Last name cannot exceed 50 characters"),
  password: passwordValidation,
  dateOfBirth: z.string().refine((date) => {
    const birthDate = new Date(date);
    const today = new Date();
    const age = today.getFullYear() - birthDate.getFullYear();
    const monthDiff = today.getMonth() - birthDate.getMonth();

    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
      return age - 1 >= 18;
    }
    return age >= 18;
  }, "You must be at least 18 years old to register"),
});

// Password change schema with validation
export const changePasswordSchema = z.object({
  currentPassword: z.string().min(1, "Current password is required"),
  newPassword: passwordValidation,
  confirmPassword: z.string(),
}).refine((data) => data.newPassword === data.confirmPassword, {
  message: "New password and confirmation must match",
  path: ["confirmPassword"],
});

export const insertPostSchema = createInsertSchema(posts).omit({
  id: true,
  authorId: true,
  createdAt: true,
  updatedAt: true,
  views: true,
  bumpedAt: true,
}).extend({
  images: z.array(z.string()).max(5, "Maximum 5 images allowed").optional(),
  willingToTravel: z.boolean().optional(),
  willingToShip: z.boolean().optional(),
  willingToTrade: z.boolean().optional(),
});

export const insertReplySchema = createInsertSchema(replies).omit({
  id: true,
  authorId: true,
  createdAt: true,
});

export const insertMessageSchema = createInsertSchema(messages).omit({
  id: true,
  senderId: true,
  createdAt: true,
  isRead: true,
});

export const insertConversationSchema = createInsertSchema(conversations).omit({
  id: true,
  createdAt: true,
  lastMessageAt: true,
});

export const updateUserPreferencesSchema = createInsertSchema(userPreferences).omit({
  id: true,
  userId: true,
  createdAt: true,
  updatedAt: true,
});

export const updateUserProfileSchema = createInsertSchema(users).omit({
  id: true,
  password: true,
  createdAt: true,
  isVerified: true,
  isAdmin: true,
  isSuspended: true,
}).partial().extend({
  currentPassword: z.string().optional(),
  newPassword: z.string().optional().refine((val) => {
    if (val && val.trim().length > 0 && val.trim().length < 6) {
      return false;
    }
    return true;
  }, "Password must be at least 6 characters"),
  confirmPassword: z.string().optional(),
}).refine(
  (data) => {
    // Only validate if password fields have content
    if (data.newPassword && data.newPassword.trim().length > 0) {
      if (!data.currentPassword || data.currentPassword.trim().length === 0) {
        return false;
      }
      if (data.newPassword !== data.confirmPassword) {
        return false;
      }
    }
    return true;
  },
  {
    message: "Password confirmation doesn't match or current password is required",
    path: ["confirmPassword"],
  }
);

export const updatePostSchema = createInsertSchema(posts).omit({
  id: true,
  authorId: true,
  createdAt: true,
  views: true,
  bumpedAt: true,
  isPinned: true,
  pinnedAt: true,
}).partial().extend({
  images: z.array(z.string()).max(5, "Maximum 5 images allowed").optional(),
});

export const loginSchema = z.object({
  username: z.string().min(1, "Username is required"),
  password: z.string().min(1, "Password is required"),
  rememberUsername: z.boolean().optional(),
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type InsertPost = z.infer<typeof insertPostSchema>;
export type Post = typeof posts.$inferSelect;
export type Category = typeof categories.$inferSelect;
export type Reply = typeof replies.$inferSelect;
export type InsertReply = z.infer<typeof insertReplySchema>;
export type Conversation = typeof conversations.$inferSelect;
export type InsertConversation = z.infer<typeof insertConversationSchema>;
export type Message = typeof messages.$inferSelect;
export type InsertMessage = z.infer<typeof insertMessageSchema>;
export type UserPreferences = typeof userPreferences.$inferSelect;
export type UpdateUserPreferences = z.infer<typeof updateUserPreferencesSchema>;
export type UpdateUserProfile = z.infer<typeof updateUserProfileSchema>;
export type UpdatePost = z.infer<typeof updatePostSchema>;
export type LoginData = z.infer<typeof loginSchema>;

export type Advertisement = typeof advertisements.$inferSelect;
export type InsertAdvertisement = typeof advertisements.$inferInsert;
export type FeaturedListing = typeof featuredListings.$inferSelect;
export type InsertFeaturedListing = typeof featuredListings.$inferInsert;

