import { useParams } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Link, useLocation } from "wouter";
import { ArrowLeft, Calendar, MessageCircle, User, Send, Shield, UserX, Flag, Key, Settings } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { 
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";

import Header from "@/components/layout/header";
import Footer from "@/components/layout/footer";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/lib/auth";
import { apiRequest } from "@/lib/queryClient";
import type { User as UserType, Post, Category } from "@shared/schema";
import { useState } from "react";

export default function PublicProfile() {
  const { id } = useParams<{ id: string }>();
  const { user: currentUser } = useAuth();
  const { toast } = useToast();
  const [location, navigate] = useLocation();
  const queryClient = useQueryClient();
  const [messageDialogOpen, setMessageDialogOpen] = useState(false);
  const [messageSubject, setMessageSubject] = useState("");
  const [messageContent, setMessageContent] = useState("");

  const { data: user } = useQuery<UserType>({
    queryKey: ["/api/users", id],
  });

  const { data: userPosts = [] } = useQuery<Post[]>({
    queryKey: ["/api/posts/user", id],
    enabled: !!id,
  });

  const { data: categories = [] } = useQuery<Category[]>({
    queryKey: ["/api/categories"],
  });

  const formatDate = (date?: Date | null) => {
    if (!date) return "Unknown";
    return new Date(date).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  };

  const getPostCountByType = (type: string) => {
    return userPosts.filter(post => {
      const category = categories.find(cat => cat.id === post.categoryId);
      return category?.type === type;
    }).length;
  };

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async ({ subject, content }: { subject: string; content: string }) => {
      if (!currentUser || !user) throw new Error("Authentication required");

      // Debug: Log what we're about to send
      console.log("ðŸ” CLIENT - About to send conversation request with user ID:", currentUser.id);
      console.log("ðŸ” CLIENT - Headers to be sent:", {
        "Content-Type": "application/json",
        "x-user-id": currentUser.id
      });

      // First create or find existing conversation - use session-based auth
      const conversationResponse = await fetch("/api/conversations", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          participant1Id: currentUser.id,
          participant2Id: user.id,
        }),
        credentials: "include",
      });

      if (!conversationResponse.ok) {
        throw new Error(`Failed to create conversation: ${conversationResponse.statusText}`);
      }

      const conversation = await conversationResponse.json();

      // Then send the message - use session-based auth
      const messageResponse = await fetch("/api/messages", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({
          conversationId: conversation.id,
          content: `Subject: ${subject}\n\n${content}`,
        }),
        credentials: "include",
      });

      if (!messageResponse.ok) {
        throw new Error(`Failed to send message: ${messageResponse.statusText}`);
      }

      const message = await messageResponse.json();

      return { conversation, message };
    },
    onSuccess: (data) => {
      setMessageDialogOpen(false);
      setMessageSubject("");
      setMessageContent("");
      toast({
        title: "Message Sent",
        description: "Your message has been sent successfully!",
      });
      // Navigate to the conversation
      navigate(`/messages?conversation=${data.conversation.id}`);
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Failed to send message",
        variant: "destructive",
      });
    },
  });

  const handleSendMessage = () => {
    if (!messageSubject.trim() || !messageContent.trim()) {
      toast({
        title: "Missing Information",
        description: "Please provide both a subject and message content.",
        variant: "destructive",
      });
      return;
    }

    sendMessageMutation.mutate({
      subject: messageSubject.trim(),
      content: messageContent.trim(),
    });
  };

  if (!user) {
    return (
      <div className="min-h-screen flex flex-col">
        <Header />
        <div className="flex-1 flex items-center justify-center">
          <Card className="w-full max-w-md">
            <CardContent className="p-8 text-center">
              <p className="text-gray-500 dark:text-gray-400">User not found or loading...</p>
            </CardContent>
          </Card>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen flex flex-col">
      <Header />

      <div className="flex-1">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          <div className="mb-6">
            <Link href="/">
              <Button variant="outline" size="sm" data-testid="button-back">
                <ArrowLeft className="w-4 h-4 mr-2" />
                Back to Forum
              </Button>
            </Link>
          </div>

          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            {/* User Profile Card */}
            <div className="lg:col-span-1">
              <Card>
                <CardHeader className="text-center">
                  <div className="flex flex-col items-center space-y-4">
                    <Avatar className="w-24 h-24">
                      <AvatarImage 
                        src={(user as any).profilePicture || undefined} 
                        alt={`${user.username}'s profile`}
                      />
                      <AvatarFallback className="text-2xl bg-forum-accent text-white">
                        {user.username.slice(0, 2).toUpperCase()}
                      </AvatarFallback>
                    </Avatar>
                    <div>
                      <h1 className="text-2xl font-bold text-forum-primary" data-testid="text-username">
                        {user.username}
                      </h1>
                      {user.firstName || user.lastName ? (
                        <p className="text-gray-600 dark:text-gray-400" data-testid="text-full-name">
                          {[user.firstName, user.lastName].filter(Boolean).join(' ')}
                        </p>
                      ) : null}
                    </div>


                    {/* Send Message Button - only show if not viewing own profile and user is logged in */}
                    {currentUser && user && currentUser.id !== user.id && (
                      <Dialog open={messageDialogOpen} onOpenChange={setMessageDialogOpen}>
                        <DialogTrigger asChild>
                          <Button 
                            variant="outline" 
                            size="sm" 
                            className="mt-4"
                            data-testid="button-send-message"
                          >
                            <MessageCircle className="w-4 h-4 mr-2" />
                            Send Message
                          </Button>
                        </DialogTrigger>
                        <DialogContent>
                          <DialogHeader>
                            <DialogTitle>Send Message to {user.username}</DialogTitle>
                            <DialogDescription>
                              Send a private message to this user. They will receive it in their messages inbox.
                            </DialogDescription>
                          </DialogHeader>
                          <div className="space-y-4">
                            <div>
                              <Label htmlFor="message-subject">Subject</Label>
                              <Input
                                id="message-subject"
                                placeholder="Enter message subject..."
                                value={messageSubject}
                                onChange={(e) => setMessageSubject(e.target.value)}
                                data-testid="input-message-subject"
                              />
                            </div>
                            <div>
                              <Label htmlFor="message-content">Message</Label>
                              <Textarea
                                id="message-content"
                                placeholder="Write your message here..."
                                value={messageContent}
                                onChange={(e) => setMessageContent(e.target.value)}
                                rows={5}
                                data-testid="textarea-message-content"
                              />
                            </div>
                            <div className="flex justify-end space-x-3">
                              <Button 
                                variant="outline" 
                                onClick={() => setMessageDialogOpen(false)}
                                data-testid="button-cancel-message"
                              >
                                Cancel
                              </Button>
                              <Button
                                onClick={handleSendMessage}
                                disabled={sendMessageMutation.isPending}
                                data-testid="button-send-message-submit"
                              >
                                <Send className="w-4 h-4 mr-2" />
                                {sendMessageMutation.isPending ? "Sending..." : "Send Message"}
                              </Button>
                            </div>
                          </div>
                        </DialogContent>
                      </Dialog>
                    )}

                    {/* MODERATOR CONTROLS - SAME AS USER PROFILE */}
                    {currentUser && (currentUser.isModerator || currentUser.isAdmin) && user && user.id !== currentUser.id && (
                      <div className="bg-red-50 dark:bg-red-950 border border-red-200 dark:border-red-700 rounded-lg p-4 mt-4">
                        <h3 className="font-semibold text-red-800 dark:text-red-100 mb-3 flex items-center">
                          <Shield className="w-4 h-4 mr-2" />
                          Moderator Actions
                          <Badge variant="secondary" className="ml-2 text-xs">
                            {currentUser.isAdmin ? 'Admin' : 'Moderator'}
                          </Badge>
                        </h3>
                        <div className="space-y-2">
                          <AlertDialog>
                            <AlertDialogTrigger asChild>
                              <Button 
                                variant="outline" 
                                size="sm" 
                                className="w-full text-red-600 hover:text-red-700 border-red-300 hover:border-red-400"
                              >
                                <UserX className="w-4 h-4 mr-2" />
                                {user?.isSuspended ? 'Unsuspend' : 'Suspend'}
                              </Button>
                            </AlertDialogTrigger>
                            <AlertDialogContent>
                              <AlertDialogHeader>
                                <AlertDialogTitle>
                                  {user?.isSuspended ? 'Unsuspend User' : 'Suspend User'}
                                </AlertDialogTitle>
                                <AlertDialogDescription>
                                  {user?.isSuspended 
                                    ? `Are you sure you want to unsuspend ${user.username}? They will regain access to post and interact on the forum.`
                                    : `Are you sure you want to suspend ${user.username}? They will lose access to post and interact on the forum.`
                                  }
                                </AlertDialogDescription>
                              </AlertDialogHeader>
                              <AlertDialogFooter>
                                <AlertDialogCancel>Cancel</AlertDialogCancel>
                                <AlertDialogAction className="bg-red-600 hover:bg-red-700">
                                  {user?.isSuspended ? 'Unsuspend' : 'Suspend'}
                                </AlertDialogAction>
                              </AlertDialogFooter>
                            </AlertDialogContent>
                          </AlertDialog>

                          <AlertDialog>
                            <AlertDialogTrigger asChild>
                              <Button 
                                variant="outline" 
                                size="sm" 
                                className="w-full text-orange-600 hover:text-orange-700 border-orange-300 hover:border-orange-400"
                              >
                                <Flag className="w-4 h-4 mr-2" />
                                Flag Username
                              </Button>
                            </AlertDialogTrigger>
                            <AlertDialogContent>
                              <AlertDialogHeader>
                                <AlertDialogTitle>Flag Username</AlertDialogTitle>
                                <AlertDialogDescription>
                                  Flag this user's username as inappropriate. They will be required to change it before they can continue using the forum.
                                </AlertDialogDescription>
                              </AlertDialogHeader>
                              <AlertDialogFooter>
                                <AlertDialogCancel>Cancel</AlertDialogCancel>
                                <AlertDialogAction className="bg-orange-600 hover:bg-orange-700">
                                  Flag Username
                                </AlertDialogAction>
                              </AlertDialogFooter>
                            </AlertDialogContent>
                          </AlertDialog>

                          <AlertDialog>
                            <AlertDialogTrigger asChild>
                              <Button 
                                variant="outline" 
                                size="sm" 
                                className="w-full text-blue-600 hover:text-blue-700 border-blue-300 hover:border-blue-400"
                              >
                                <Key className="w-4 h-4 mr-2" />
                                Flag Password
                              </Button>
                            </AlertDialogTrigger>
                            <AlertDialogContent>
                              <AlertDialogHeader>
                                <AlertDialogTitle>Flag Password Reset</AlertDialogTitle>
                                <AlertDialogDescription>
                                  Flag this user for a password reset. They will be required to change their password on next login.
                                </AlertDialogDescription>
                              </AlertDialogHeader>
                              <AlertDialogFooter>
                                <AlertDialogCancel>Cancel</AlertDialogCancel>
                                <AlertDialogAction className="bg-blue-600 hover:bg-blue-700">
                                  Flag Password Reset
                                </AlertDialogAction>
                              </AlertDialogFooter>
                            </AlertDialogContent>
                          </AlertDialog>

                          {currentUser.isAdmin && (
                            <AlertDialog>
                              <AlertDialogTrigger asChild>
                                <Button 
                                  variant="outline" 
                                  size="sm" 
                                  className="w-full text-purple-600 hover:text-purple-700 border-purple-300 hover:border-purple-400"
                                >
                                  <Settings className="w-4 h-4 mr-2" />
                                  {user?.isModerator ? 'Remove Mod' : 'Make Mod'}
                                </Button>
                              </AlertDialogTrigger>
                              <AlertDialogContent>
                                <AlertDialogHeader>
                                  <AlertDialogTitle>
                                    {user?.isModerator ? 'Remove Moderator Status' : 'Grant Moderator Status'}
                                  </AlertDialogTitle>
                                  <AlertDialogDescription>
                                    {user?.isModerator 
                                      ? `Remove moderator privileges from ${user.username}? They will no longer be able to moderate posts and users.`
                                      : `Grant moderator privileges to ${user.username}? They will be able to moderate posts and users.`
                                    }
                                  </AlertDialogDescription>
                                </AlertDialogHeader>
                                <AlertDialogFooter>
                                  <AlertDialogCancel>Cancel</AlertDialogCancel>
                                  <AlertDialogAction className="bg-purple-600 hover:bg-purple-700">
                                    {user?.isModerator ? 'Remove Moderator' : 'Grant Moderator'}
                                  </AlertDialogAction>
                                </AlertDialogFooter>
                              </AlertDialogContent>
                            </AlertDialog>
                          )}
                        </div>
                      </div>
                    )}
                  </div>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="flex items-center space-x-2 text-sm text-gray-500">
                    <Calendar className="w-4 h-4" />
                    <span data-testid="text-joined-date">
                      Joined {formatDate(user.createdAt)}
                    </span>
                  </div>

                  <div className="pt-4 border-t border-gray-200 dark:border-gray-700">
                    <h3 className="font-medium text-forum-primary mb-3">Activity</h3>
                    <div className="space-y-2">
                      <div className="flex justify-between items-center">
                        <span className="text-sm text-gray-600 dark:text-gray-400">Total Posts</span>
                        <Badge variant="outline" data-testid="text-total-posts">
                          {userPosts.length}
                        </Badge>
                      </div>
                      <div className="flex justify-between items-center">
                        <span className="text-sm text-gray-600 dark:text-gray-400">For Sale Posts</span>
                        <Badge variant="default" data-testid="text-wts-posts">
                          {getPostCountByType('wts')}
                        </Badge>
                      </div>
                      <div className="flex justify-between items-center">
                        <span className="text-sm text-gray-600 dark:text-gray-400">Want to Buy Posts</span>
                        <Badge variant="secondary" data-testid="text-wtb-posts">
                          {getPostCountByType('wtb')}
                        </Badge>
                      </div>
                      <div className="flex justify-between items-center">
                        <span className="text-sm text-gray-600 dark:text-gray-400">Discussion Posts</span>
                        <Badge variant="outline" data-testid="text-discussion-posts">
                          {getPostCountByType('discussion')}
                        </Badge>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </div>

            {/* User Posts */}
            <div className="lg:col-span-2">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <MessageCircle className="w-5 h-5" />
                    <span>Recent Posts by {user.username}</span>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  {userPosts.length === 0 ? (
                    <div className="text-center py-8">
                      <User className="w-12 h-12 mx-auto text-gray-300 dark:text-gray-600 mb-4" />
                      <p className="text-gray-500 dark:text-gray-400" data-testid="text-no-posts">
                        {user.username} hasn't created any posts yet.
                      </p>
                    </div>
                  ) : (
                    <div className="space-y-4">
                      {userPosts.slice(0, 10).map((post) => {
                        const category = categories.find(cat => cat.id === post.categoryId);
                        const getBadgeVariant = (type?: string) => {
                          switch (type) {
                            case "wts": return "default";
                            case "wtb": return "secondary";
                            case "discussion": return "outline";
                            default: return "outline";
                          }
                        };

                        const getBadgeText = (type?: string) => {
                          switch (type) {
                            case "wts": return "WTS";
                            case "wtb": return "WTB";
                            case "discussion": return "Discussion";
                            default: return "Post";
                          }
                        };

                        return (
                          <div 
                            key={post.id} 
                            className="border border-gray-200 dark:border-gray-700 rounded-lg p-4 hover:bg-gray-50 dark:hover:bg-gray-800 transition-colors"
                            data-testid={`post-${post.id}`}
                          >
                            <div className="flex items-start justify-between mb-2">
                              <div className="flex items-center space-x-2">
                                <Badge variant={getBadgeVariant(category?.type)}>
                                  {getBadgeText(category?.type)}
                                </Badge>
                                {category && (
                                  <span className="text-sm text-gray-500">in {category.name}</span>
                                )}
                              </div>
                              <span className="text-xs text-gray-400">
                                {formatDate(post.createdAt)}
                              </span>
                            </div>
                            <Link href={`/posts/${post.id}`}>
                              <h4 className="font-medium text-forum-primary hover:text-forum-accent cursor-pointer mb-2">
                                {post.title}
                              </h4>
                            </Link>
                            <p className="text-sm text-gray-600 dark:text-gray-400 line-clamp-2">
                              {post.content.length > 150 ? `${post.content.slice(0, 150)}...` : post.content}
                            </p>
                            <div className="flex items-center space-x-4 mt-3 text-xs text-gray-500">
                              {post.views !== undefined && (
                                <span>{post.views} views</span>
                              )}
                              {post.price && (
                                <span>${(post.price / 100).toLocaleString()}</span>
                              )}
                            </div>
                          </div>
                        );
                      })}
                      {userPosts.length > 10 && (
                        <div className="text-center pt-4">
                          <p className="text-sm text-gray-500">
                            Showing 10 of {userPosts.length} posts
                          </p>
                        </div>
                      )}
                    </div>
                  )}
                </CardContent>
              </Card>
            </div>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
}

client/src/pages/search.tsx.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useLocation, Link } from "wouter";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Search, MessageSquare, Calendar, DollarSign, ArrowLeft, MapPin, Eye } from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import type { Post, Category } from "@shared/schema";

interface SearchResult {
  posts: any[];
  users: any[];
  totalResults: number;
}

export default function SearchPage() {
  const [location, navigate] = useLocation();
  const [searchTerm, setSearchTerm] = useState("");
  const [currentQuery, setCurrentQuery] = useState("");

  // Get search query from URL
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const query = params.get('q') || '';
    setSearchTerm(query);
    setCurrentQuery(query);
  }, [location]);

  const { data: categories = [] } = useQuery<Category[]>({
    queryKey: ["/api/categories"],
  });

  const { data: searchResults, isLoading } = useQuery<SearchResult>({
    queryKey: ["/api/search", currentQuery],
    enabled: !!currentQuery.trim(),
    queryFn: async () => {
      const res = await fetch(`/api/search?q=${encodeURIComponent(currentQuery)}`, {
        credentials: "include",
      });

      if (!res.ok) {
        throw new Error(`Search failed: ${res.status}`);
      }

      return res.json();
    },
  });

  const getCategoryName = (categoryId: string) => {
    const category = categories.find(c => c.id === categoryId);
    return category?.name || 'Unknown';
  };

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      const newQuery = searchTerm.trim();
      setCurrentQuery(newQuery);
      navigate(`/search?q=${encodeURIComponent(newQuery)}`);
    }
  };

  return (
    <div className="max-w-6xl mx-auto p-6">
      <div className="flex items-center gap-4 mb-6">
        <Button 
          onClick={() => navigate("/")} 
          variant="outline" 
          size="sm"
          data-testid="button-back"
        >
          <ArrowLeft className="h-4 w-4 mr-2" />
          Back to Forum
        </Button>

        <form onSubmit={handleSearch} className="flex-1 max-w-2xl">
          <div className="relative">
            <Input
              type="text"
              placeholder="Search posts, titles, users..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pr-12"
              data-testid="input-search-page"
            />
            <Button
              type="submit"
              variant="ghost"
              size="sm"
              className="absolute right-1 top-1/2 -translate-y-1/2"
              data-testid="button-search-page"
            >
              <Search className="h-4 w-4" />
            </Button>
          </div>
        </form>
      </div>

      {currentQuery && (
        <div className="mb-6">
          <h1 className="text-2xl font-bold text-gray-900 dark:text-white mb-2">
            Search Results for "{currentQuery}"
          </h1>
          {searchResults && (
            <p className="text-gray-600 dark:text-gray-400">
              Found {searchResults.totalResults} result{searchResults.totalResults !== 1 ? 's' : ''}
            </p>
          )}
        </div>
      )}

      {isLoading && (
        <div className="text-center py-12">
          <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-forum-primary"></div>
          <p className="mt-4 text-gray-600 dark:text-gray-400">Searching...</p>
        </div>
      )}

      {searchResults && searchResults.users.length > 0 && (
        <div className="space-y-4 mb-8">
          <h2 className="text-xl font-semibold text-gray-900 dark:text-white">Users</h2>
          {searchResults.users.map((user) => (
            <Card key={user.id} className="hover:shadow-md transition-shadow">
              <CardContent className="p-4">
                <div className="flex items-center space-x-4">
                  <div className="w-12 h-12 bg-forum-primary rounded-full flex items-center justify-center text-white font-semibold text-lg">
                    {user.username.charAt(0).toUpperCase()}
                  </div>
                  <div className="flex-1">
                    <div className="flex items-center space-x-2">
                      <Link 
                        href={`/user/${user.id}`} 
                        className="text-lg font-semibold text-blue-600 dark:text-blue-400 hover:underline"
                        data-testid={`link-user-${user.id}`}
                      >
                        {user.username}
                      </Link>
                      {user.isVerified && (
                        <Badge variant="secondary" className="text-xs">Verified</Badge>
                      )}
                    </div>
                    {(user.firstName || user.lastName) && (
                      <p className="text-gray-600 dark:text-gray-400">
                        {user.firstName} {user.lastName}
                      </p>
                    )}
                    {user.location && (
                      <p className="text-sm text-gray-500 dark:text-gray-400">
                        <MapPin className="w-3 h-3 inline mr-1" />
                        {user.location}
                      </p>
                    )}
                    {user.bio && (
                      <p className="text-sm text-gray-600 dark:text-gray-400 mt-1 line-clamp-2">
                        {user.bio}
                      </p>
                    )}
                  </div>
                  <div className="text-right">
                    <p className="text-sm text-gray-500 dark:text-gray-400">
                      <Calendar className="w-3 h-3 inline mr-1" />
                      Joined {formatDistanceToNow(new Date(user.createdAt), { addSuffix: true })}
                    </p>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {searchResults && searchResults.posts.length > 0 && (
        <div className="space-y-4">
          <h2 className="text-xl font-semibold text-gray-900 dark:text-white">Posts</h2>
          {searchResults.posts.map((post) => (
            <Card key={post.id} className="hover:shadow-md transition-shadow">
              <CardHeader className="pb-3">
                <div className="flex items-center justify-between">
                  <Badge variant="secondary" className="mb-2">
                    {getCategoryName(post.categoryId)}
                  </Badge>
                  {post.price && (
                    <div className="flex items-center text-green-600 font-semibold">
                      <DollarSign className="h-4 w-4 mr-1" />
                      {post.price}
                    </div>
                  )}
                </div>
                <CardTitle className="text-lg">
                  <Link 
                    href={`/posts/${post.id}`} 
                    className="text-blue-600 dark:text-blue-400 hover:underline"
                    data-testid={`link-post-${post.id}`}
                  >
                    {post.title}
                  </Link>
                </CardTitle>
              </CardHeader>
              <CardContent>
                <p className="text-gray-600 dark:text-gray-400 mb-4 line-clamp-3">
                  {post.content}
                </p>
                <div className="flex items-center justify-between text-sm text-gray-500 dark:text-gray-400">
                  <div className="flex items-center space-x-4">
                    <span>By {(post as any).username || 'Anonymous'}</span>
                    <div className="flex items-center">
                      <Calendar className="h-4 w-4 mr-1" />
                      {post.createdAt ? formatDistanceToNow(new Date(post.createdAt), { addSuffix: true }) : 'Unknown date'}
                    </div>
                  </div>
                  <div className="flex items-center space-x-3">
                    <div className="flex items-center">
                      <MessageSquare className="h-4 w-4 mr-1" />
                      {(post as any).replyCount || 0} replies
                    </div>
                    <div className="flex items-center">
                      <Eye className="h-4 w-4 mr-1" />
                      {post.views || 0} views
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      {searchResults && searchResults.posts.length === 0 && searchResults.users.length === 0 && currentQuery && !isLoading && (
        <div className="text-center py-12">
          <Search className="h-12 w-12 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">No results found</h3>
          <p className="text-gray-600 dark:text-gray-400 mb-4">
            Try different keywords or check your spelling
          </p>
          <Button 
            onClick={() => navigate("/")} 
            variant="outline"
            data-testid="button-browse-forum"
          >
            Browse Forum
          </Button>
        </div>
      )}

      {!currentQuery && (
        <div className="text-center py-12">
          <Search className="h-12 w-12 text-gray-400 mx-auto mb-4" />
          <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-2">Search the Forum</h3>
          <p className="text-gray-600 dark:text-gray-400">
            Enter keywords to find posts, discussions, and users
          </p>
        </div>
      )}
    </div>
  );
}

client/src/components/advertising/ad-con.content:
import { useQuery } from "@tanstack/react-query";
import BannerAd from "./banner-ad";
import type { Advertisement } from "@shared/schema";

interface AdContainerProps {
  position: "header" | "sidebar" | "footer" | "in-feed";
  className?: string;
}

export default function AdContainer({ position, className = "" }: AdContainerProps) {
  const { data: ads = [] } = useQuery<Advertisement[]>({
    queryKey: ["/api/advertisements", position],
  });

  const activeAds = ads.filter(ad => {
    if (!ad.isActive) return false;
    if (ad.endDate && new Date(ad.endDate) < new Date()) return false;
    return true;
  });

  if (activeAds.length === 0) return null;

  // For now, show the first active ad. Could implement rotation logic here
  const currentAd = activeAds[0];

  return (
    <div className={className}>
      <BannerAd
        id={currentAd.id}
        title={currentAd.title}
        description={currentAd.description}
        imageUrl={currentAd.imageUrl || undefined}
        targetUrl={currentAd.targetUrl}
        sponsor={currentAd.sponsor}
        position={position}
        size={currentAd.size as "small" | "medium" | "large"}
        dismissible={position === "sidebar"}
      />
    </div>
  );
}

client/src/components/advertising/banner.content:
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { ExternalLink, X } from "lucide-react";
import { useState } from "react";

interface BannerAdProps {
  id: string;
  title: string;
  description: string;
  imageUrl?: string;
  targetUrl: string;
  sponsor: string;
  position: "header" | "sidebar" | "footer" | "in-feed";
  size?: "small" | "medium" | "large";
  dismissible?: boolean;
}

export default function BannerAd({ 
  id, 
  title, 
  description, 
  imageUrl, 
  targetUrl, 
  sponsor, 
  position,
  size = "medium",
  dismissible = false 
}: BannerAdProps) {
  const [isDismissed, setIsDismissed] = useState(false);

  if (isDismissed) return null;

  const handleAdClick = () => {
    // Track ad click analytics here
    console.log(`Ad clicked: ${id} - ${title}`);
    window.open(targetUrl, '_blank', 'noopener,noreferrer');
  };

  const getSizeClasses = () => {
    switch (size) {
      case "small": return "h-20";
      case "large": return "h-40 md:h-32";
      default: return "h-28";
    }
  };

  const getPositionClasses = () => {
    switch (position) {
      case "header": return "mb-4";
      case "footer": return "mt-4";
      case "sidebar": return "mb-4";
      case "in-feed": return "my-4";
      default: return "";
    }
  };

  return (
    <Card 
      className={`bg-yellow-50 dark:bg-yellow-950 border-yellow-200 dark:border-yellow-800 cursor-pointer hover:shadow-md transition-shadow ${getSizeClasses()} ${getPositionClasses()}`}
      onClick={handleAdClick}
      data-testid={`banner-ad-${id}`}
    >
      <CardContent className="p-3 h-full relative">
        {dismissible && (
          <button
            onClick={(e) => {
              e.stopPropagation();
              setIsDismissed(true);
            }}
            className="absolute top-2 right-2 text-gray-400 hover:text-gray-600 z-10"
            data-testid={`button-dismiss-ad-${id}`}
          >
            <X className="w-4 h-4" />
          </button>
        )}

        <div className="flex items-center h-full space-x-3">
          {imageUrl && (
            <div className="flex-shrink-0">
              <img 
                src={imageUrl} 
                alt={sponsor}
                className="w-16 h-16 object-cover rounded"
                onError={(e) => {
                  e.currentTarget.style.display = 'none';
                }}
              />
            </div>
          )}

          <div className="flex-1 min-w-0">
            <div className="flex items-center space-x-2 mb-1">
              <Badge variant="outline" className="bg-yellow-100 dark:bg-yellow-900 text-xs">
                Sponsored
              </Badge>
              <span className="text-xs text-gray-500 dark:text-gray-400">
                by {sponsor}
              </span>
            </div>

            <h3 className="font-semibold text-sm text-gray-900 dark:text-gray-100 mb-1 line-clamp-1">
              {title}
            </h3>

            <p className="text-xs text-gray-600 dark:text-gray-300 line-clamp-2">
              {description}
            </p>

            <div className="flex items-center mt-2">
              <ExternalLink className="w-3 h-3 mr-1 text-gray-400" />
              <span className="text-xs text-gray-400">Click to learn more</span>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/advertising/featur.content:
import { Link } from "wouter";
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { MapPin, DollarSign, Eye, MessageSquare, TrendingUp, Star, Crown } from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { Post, User, FeaturedListing } from "@shared/schema";

interface FeaturedPostCardProps {
  post: Post;
  author?: User;
  replyCount?: number;
  category?: {
    name: string;
    type: string;
  };
  currentUser?: User | null;
  featuredListing: FeaturedListing;
}

export default function FeaturedPostCard({ 
  post, 
  author, 
  replyCount = 0, 
  category, 
  currentUser, 
  featuredListing 
}: FeaturedPostCardProps) {
  const { toast } = useToast();

  const bumpPostMutation = useMutation({
    mutationFn: async () => {
      return apiRequest(`/api/posts/${post.id}/bump`, { method: "POST", body: { authorId: currentUser?.id } });
    },
    onSuccess: (data) => {
      queryClient.invalidateQueries({ queryKey: ["/api/posts"] });
      toast({ title: "Post bumped successfully!" });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to bump post",
        variant: "destructive",
      });
    },
  });

  const canBump = currentUser && currentUser.id === post.authorId;
  const formatPrice = (priceInCents?: number | null) => {
    if (!priceInCents) return null;
    return `$${(priceInCents / 100).toLocaleString()}`;
  };

  const getTimeAgo = (date?: Date | string | null) => {
    if (!date) return "Unknown";
    const dateObj = typeof date === 'string' ? new Date(date) : date;
    const now = new Date();
    const diffInHours = Math.floor((now.getTime() - dateObj.getTime()) / (1000 * 60 * 60));

    if (diffInHours < 1) return "Less than 1 hour ago";
    if (diffInHours < 24) return `${diffInHours} hours ago`;
    const diffInDays = Math.floor(diffInHours / 24);
    return `${diffInDays} days ago`;
  };

  const getBadgeVariant = (type?: string) => {
    switch (type) {
      case "wts": return "default";
      case "wtb": return "secondary";
      case "discussion": return "outline";
      default: return "outline";
    }
  };

  const getBadgeText = (type?: string) => {
    switch (type) {
      case "wts": return "WTS";
      case "wtb": return "WTB";
      case "discussion": return "Discussion";
      default: return "Post";
    }
  };

  const isFeaturedActive = featuredListing.isActive && new Date(featuredListing.featuredUntil) > new Date();

  return (
    <Card 
      className={`transition-all duration-300 ${
        isFeaturedActive 
          ? "bg-gradient-to-r from-yellow-50 to-amber-50 dark:from-yellow-950/30 dark:to-amber-950/30 border-2 border-yellow-300 dark:border-yellow-600 shadow-lg hover:shadow-xl" 
          : "hover:shadow-md"
      }`}
      data-testid={`card-featured-post-${post.id}`}
    >
      <CardContent className="p-6 relative">
        {/* Featured Badge */}
        {isFeaturedActive && (
          <div className="absolute -top-2 -right-2 z-10">
            <div className="bg-gradient-to-r from-yellow-400 to-amber-500 text-white px-3 py-1 rounded-full text-xs font-bold flex items-center shadow-lg">
              <Crown className="w-3 h-3 mr-1" />
              Featured
            </div>
          </div>
        )}

        <div className="flex items-start space-x-4">
          <div className={`w-12 h-12 rounded-full flex items-center justify-center text-white font-medium ${
            isFeaturedActive 
              ? "bg-gradient-to-br from-yellow-500 to-amber-600 shadow-lg" 
              : "bg-forum-accent"
          }`}>
            <span data-testid={`text-author-initials-${post.id}`}>
              {author?.username?.slice(0, 2).toUpperCase() || "??"}
            </span>
          </div>

          <div className="flex-1 min-w-0">
            <div className="flex items-center space-x-2 mb-1">
              {author?.id ? (
                <Link href={`/user/${author.id}`}>
                  <span className={`font-medium cursor-pointer transition-colors ${
                    isFeaturedActive 
                      ? "text-amber-800 dark:text-amber-200 hover:text-amber-600 dark:hover:text-amber-300" 
                      : "text-forum-primary hover:text-forum-accent"
                  }`} data-testid={`text-author-${post.id}`}>
                    {author.username || "Unknown User"}
                  </span>
                </Link>
              ) : (
                <span className="font-medium text-forum-primary" data-testid={`text-author-${post.id}`}>
                  Unknown User
                </span>
              )}

              <Badge variant={getBadgeVariant(category?.type)} data-testid={`badge-type-${post.id}`}>
                {getBadgeText(category?.type)}
              </Badge>

              {isFeaturedActive && (
                <Badge variant="outline" className="bg-yellow-100 dark:bg-yellow-900 text-yellow-800 dark:text-yellow-200">
                  <Star className="w-3 h-3 mr-1 fill-current" />
                  Promoted
                </Badge>
              )}

              <span className="text-xs text-gray-500 dark:text-gray-400" data-testid={`text-timestamp-${post.id}`}>
                {getTimeAgo(post.createdAt)}
              </span>
            </div>

            <Link href={`/posts/${post.id}`}>
              <h3 className={`text-xl font-bold mb-2 cursor-pointer transition-colors ${
                isFeaturedActive 
                  ? "text-amber-900 dark:text-amber-100 hover:text-amber-700 dark:hover:text-amber-200" 
                  : "text-forum-primary hover:text-forum-accent"
              }`} data-testid={`link-post-title-${post.id}`}>
                {post.title}
              </h3>
            </Link>

            <p className="text-gray-700 dark:text-gray-200 text-sm mb-3 line-clamp-3" data-testid={`text-content-${post.id}`}>
              {post.content.length > 200 ? `${post.content.slice(0, 200)}...` : post.content}
            </p>

            <div className="flex items-center justify-between">
              <div className="flex items-center space-x-4 text-sm text-gray-600 dark:text-gray-300">
                {post.location && (
                  <span className="flex items-center" data-testid={`text-location-${post.id}`}>
                    <MapPin className="w-4 h-4 mr-1" />
                    {post.location}
                  </span>
                )}
                {post.price && (
                  <span className="flex items-center font-semibold text-green-600 dark:text-green-400" data-testid={`text-price-${post.id}`}>
                    <DollarSign className="w-4 h-4 mr-1" />
                    {formatPrice(post.price)}
                  </span>
                )}
                <span className="flex items-center" data-testid={`text-views-${post.id}`}>
                  <Eye className="w-4 h-4 mr-1" />
                  {post.views || 0} views
                </span>
                {replyCount > 0 && (
                  <span className="flex items-center" data-testid={`text-replies-${post.id}`}>
                    <MessageSquare className="w-4 h-4 mr-1" />
                    {replyCount} replies
                  </span>
                )}
              </div>

              {canBump && (
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => bumpPostMutation.mutate()}
                  disabled={bumpPostMutation.isPending}
                  data-testid={`button-bump-${post.id}`}
                  className="ml-4"
                >
                  <TrendingUp className="w-4 h-4 mr-1" />
                  Bump
                </Button>
              )}
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/auth/change-passwo.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { AlertCircle, CheckCircle2, Eye, EyeOff } from "lucide-react";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { getPasswordRequirements } from "@shared/password-utils";

const passwordValidation = z.string()
  .min(10, "Password must be at least 10 characters long")
  .regex(/^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]/, 
    "Password must contain at least one lowercase letter, one uppercase letter, one number, and one special character (@$!%*?&)");

const changePasswordSchema = z.object({
  currentPassword: z.string().min(1, "Current password is required"),
  newPassword: passwordValidation,
  confirmPassword: z.string(),
}).refine((data) => data.newPassword === data.confirmPassword, {
  message: "New password and confirmation must match",
  path: ["confirmPassword"],
});

type ChangePasswordFormData = z.infer<typeof changePasswordSchema>;

export function ChangePasswordForm() {
  const [showCurrentPassword, setShowCurrentPassword] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [success, setSuccess] = useState<string | null>(null);

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors },
    watch,
  } = useForm<ChangePasswordFormData>({
    resolver: zodResolver(changePasswordSchema),
  });

  const newPassword = watch("newPassword");

  const changePasswordMutation = useMutation({
    mutationFn: async (data: ChangePasswordFormData) => {
      const userId = sessionStorage.getItem("userId");
      if (!userId) {
        throw new Error("User not authenticated");
      }

      return apiRequest("/api/auth/change-password", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "X-User-ID": userId,
        },
        body: JSON.stringify(data),
      });
    },
    onSuccess: (data) => {
      setSuccess(data.message);
      reset();
      // Clear success message after 5 seconds
      setTimeout(() => setSuccess(null), 5000);
    },
  });

  const onSubmit = (data: ChangePasswordFormData) => {
    setSuccess(null);
    changePasswordMutation.mutate(data);
  };

  const passwordRequirements = getPasswordRequirements();

  return (
    <Card className="w-full max-w-md mx-auto" data-testid="change-password-card">
      <CardHeader>
        <CardTitle data-testid="change-password-title">Change Password</CardTitle>
      </CardHeader>
      <CardContent>
        {success && (
          <Alert className="mb-4" data-testid="change-password-success">
            <CheckCircle2 className="h-4 w-4" />
            <AlertDescription>{success}</AlertDescription>
          </Alert>
        )}

        {changePasswordMutation.error && (
          <Alert variant="destructive" className="mb-4" data-testid="change-password-error">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>
              {(changePasswordMutation.error as Error).message}
            </AlertDescription>
          </Alert>
        )}

        <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="currentPassword" data-testid="label-current-password">
              Current Password
            </Label>
            <div className="relative">
              <Input
                id="currentPassword"
                type={showCurrentPassword ? "text" : "password"}
                {...register("currentPassword")}
                className={errors.currentPassword ? "border-red-500" : ""}
                data-testid="input-current-password"
              />
              <Button
                type="button"
                variant="ghost"
                size="sm"
                className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                onClick={() => setShowCurrentPassword(!showCurrentPassword)}
                data-testid="toggle-current-password"
              >
                {showCurrentPassword ? (
                  <EyeOff className="h-4 w-4" />
                ) : (
                  <Eye className="h-4 w-4" />
                )}
              </Button>
            </div>
            {errors.currentPassword && (
              <p className="text-sm text-red-500" data-testid="error-current-password">
                {errors.currentPassword.message}
              </p>
            )}
          </div>

          <div className="space-y-2">
            <Label htmlFor="newPassword" data-testid="label-new-password">
              New Password
            </Label>
            <div className="relative">
              <Input
                id="newPassword"
                type={showNewPassword ? "text" : "password"}
                {...register("newPassword")}
                className={errors.newPassword ? "border-red-500" : ""}
                data-testid="input-new-password"
              />
              <Button
                type="button"
                variant="ghost"
                size="sm"
                className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                onClick={() => setShowNewPassword(!showNewPassword)}
                data-testid="toggle-new-password"
              >
                {showNewPassword ? (
                  <EyeOff className="h-4 w-4" />
                ) : (
                  <Eye className="h-4 w-4" />
                )}
              </Button>
            </div>
            {errors.newPassword && (
              <p className="text-sm text-red-500" data-testid="error-new-password">
                {errors.newPassword.message}
              </p>
            )}

            {/* Password Requirements */}
            <div className="mt-2 p-3 bg-muted rounded-md" data-testid="password-requirements">
              <p className="text-sm font-medium mb-2">Password Requirements:</p>
              <ul className="text-xs space-y-1">
                {passwordRequirements.map((req, index) => (
                  <li key={index} className="flex items-center gap-2" data-testid={`requirement-${index}`}>
                    <div className="w-1.5 h-1.5 bg-muted-foreground rounded-full" />
                    {req}
                  </li>
                ))}
              </ul>
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="confirmPassword" data-testid="label-confirm-password">
              Confirm New Password
            </Label>
            <div className="relative">
              <Input
                id="confirmPassword"
                type={showConfirmPassword ? "text" : "password"}
                {...register("confirmPassword")}
                className={errors.confirmPassword ? "border-red-500" : ""}
                data-testid="input-confirm-password"
              />
              <Button
                type="button"
                variant="ghost"
                size="sm"
                className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                data-testid="toggle-confirm-password"
              >
                {showConfirmPassword ? (
                  <EyeOff className="h-4 w-4" />
                ) : (
                  <Eye className="h-4 w-4" />
                )}
              </Button>
            </div>
            {errors.confirmPassword && (
              <p className="text-sm text-red-500" data-testid="error-confirm-password">
                {errors.confirmPassword.message}
              </p>
            )}
          </div>

          <Button 
            type="submit" 
            className="w-full" 
            disabled={changePasswordMutation.isPending}
            data-testid="button-change-password"
          >
            {changePasswordMutation.isPending ? "Changing Password..." : "Change Password"}
          </Button>
        </form>
      </CardContent>
    </Card>
  );
}

