import React, { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Eye, EyeOff } from "lucide-react";
import { validatePassword } from "@shared/password-utils";

interface ForcedPasswordChangeProps {
  user: any;
  onPasswordChanged: () => void;
}

export function ForcedPasswordChange({ user, onPasswordChanged }: ForcedPasswordChangeProps) {
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showCurrentPassword, setShowCurrentPassword] = useState(false);
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [errors, setErrors] = useState<Record<string, string>>({});
  const { toast } = useToast();

  const validateForm = () => {
    const newErrors: Record<string, string> = {};

    if (!currentPassword) {
      newErrors.currentPassword = "Current password is required";
    }

    const passwordValidation = validatePassword(newPassword);
    if (!passwordValidation.isValid) {
      newErrors.newPassword = passwordValidation.errors[0];
    }

    if (newPassword !== confirmPassword) {
      newErrors.confirmPassword = "Passwords do not match";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsLoading(true);

    try {
      await apiRequest("/api/auth/change-password", {
        method: "POST",
        headers: {
          "x-user-id": user.id
        },
        body: {
          currentPassword,
          newPassword,
          confirmPassword
        },
      });

      toast({
        title: "Password Updated",
        description: "Your password has been successfully updated with the new security requirements.",
        variant: "default",
      });

      onPasswordChanged();
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to update password",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Dialog open={true} onOpenChange={() => {}}>
      <DialogContent className="w-full max-w-md">
        <DialogHeader>
          <DialogTitle className="text-2xl text-red-600 dark:text-red-400 text-center">
            Password Update Required
          </DialogTitle>
          <DialogDescription id="password-reset-description" className="text-base text-center">
            Your current password doesn't meet our new security requirements. Please update your password to continue.
          </DialogDescription>
        </DialogHeader>
        <div className="p-6">
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="currentPassword">Current Password</Label>
              <div className="relative">
                <Input
                  id="currentPassword"
                  type={showCurrentPassword ? "text" : "password"}
                  value={currentPassword}
                  onChange={(e) => setCurrentPassword(e.target.value)}
                  className={errors.currentPassword ? "border-red-500" : ""}
                  data-testid="input-current-password"
                />
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                  onClick={() => setShowCurrentPassword(!showCurrentPassword)}
                  data-testid="button-toggle-current-password"
                  tabIndex={-1}
                >
                  {showCurrentPassword ? (
                    <EyeOff className="h-4 w-4" />
                  ) : (
                    <Eye className="h-4 w-4" />
                  )}
                </Button>
              </div>
              {errors.currentPassword && (
                <p className="text-sm text-red-500">{errors.currentPassword}</p>
              )}
            </div>

            <div className="space-y-2">
              <Label htmlFor="newPassword">New Password</Label>
              <div className="relative">
                <Input
                  id="newPassword"
                  type={showNewPassword ? "text" : "password"}
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  className={errors.newPassword ? "border-red-500" : ""}
                  data-testid="input-new-password"
                />
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                  onClick={() => setShowNewPassword(!showNewPassword)}
                  data-testid="button-toggle-new-password"
                  tabIndex={-1}
                >
                  {showNewPassword ? (
                    <EyeOff className="h-4 w-4" />
                  ) : (
                    <Eye className="h-4 w-4" />
                  )}
                </Button>
              </div>
              {errors.newPassword && (
                <p className="text-sm text-red-500">{errors.newPassword}</p>
              )}
              <div className="text-sm text-muted-foreground">
                <p>Password requirements:</p>
                <ul className="list-disc list-inside space-y-1 ml-2">
                  <li>At least 10 characters long</li>
                  <li>Contains at least one uppercase letter</li>
                  <li>Contains at least one lowercase letter</li>
                  <li>Contains at least one number</li>
                  <li>Contains at least one special character</li>
                </ul>
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="confirmPassword">Confirm New Password</Label>
              <div className="relative">
                <Input
                  id="confirmPassword"
                  type={showConfirmPassword ? "text" : "password"}
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  className={errors.confirmPassword ? "border-red-500" : ""}
                  data-testid="input-confirm-password"
                />
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  className="absolute right-0 top-0 h-full px-3 py-2 hover:bg-transparent"
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                  data-testid="button-toggle-confirm-password"
                  tabIndex={-1}
                >
                  {showConfirmPassword ? (
                    <EyeOff className="h-4 w-4" />
                  ) : (
                    <Eye className="h-4 w-4" />
                  )}
                </Button>
              </div>
              {errors.confirmPassword && (
                <p className="text-sm text-red-500">{errors.confirmPassword}</p>
              )}
            </div>

            <Button 
              type="submit" 
              className="w-full" 
              disabled={isLoading}
              data-testid="button-update-password"
            >
              {isLoading ? "Updating..." : "Update Password"}
            </Button>
          </form>

          <div className="mt-4 p-3 bg-yellow-50 dark:bg-yellow-900/20 border border-yellow-200 dark:border-yellow-800 rounded-md">
            <p className="text-sm text-yellow-800 dark:text-yellow-200">
              <strong>Security Notice:</strong> We've updated our password requirements to better protect your account. 
              Your new password will be securely encrypted and must meet the requirements shown above.
            </p>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/auth/login-form.ts.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation } from "@tanstack/react-query";
import { loginSchema, type LoginData } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Checkbox } from "@/components/ui/checkbox";
import { Eye, EyeOff } from "lucide-react";
import { ForcedPasswordChange } from "@/components/auth/forced-password-change";
import UsernameChangeForm from "./username-change-form";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useActivityTracker } from "@/lib/activity-tracker";
import { UsernameStorage } from "@/lib/username-storage";
import { queryClient } from "@/lib/queryClient";

interface LoginFormProps {
  onSuccess: () => void;
}

export default function LoginForm({ onSuccess }: LoginFormProps) {
  const { toast } = useToast();
  const { startTrackingUser } = useActivityTracker();
  const [error, setError] = useState<string>("");
  const [showPassword, setShowPassword] = useState(false);

  const [showPasswordReset, setShowPasswordReset] = useState(false);
  const [userRequiringReset, setUserRequiringReset] = useState<any>(null);
  const [showUsernameChange, setShowUsernameChange] = useState(false);
  const [userRequiringUsernameChange, setUserRequiringUsernameChange] = useState<any>(null);


  const form = useForm<LoginData>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      username: UsernameStorage.getMostRecentUsername(),
      password: "",
      rememberUsername: true,
    },
  });

  const loginMutation = useMutation({
    mutationFn: async (credentials: LoginData) => {
      const response = await apiRequest("/api/auth/login", { method: "POST", body: credentials });
      return response;
    },
    onSuccess: (user) => {
      const formData = form.getValues();

      // Save username if remember username is checked
      if (formData.rememberUsername && formData.username) {
        UsernameStorage.saveUsername(formData.username);
      }

      // Check if user needs password reset
      if (user.requirePasswordReset) {
        setUserRequiringReset(user);
        setShowPasswordReset(true);
        toast({
          title: "Password Update Required",
          description: user.message || "Your password must be updated to meet new security requirements.",
          variant: "destructive",
        });
        return;
      }

      // Check if user needs username change
      if (user.requireUsernameChange) {
        setUserRequiringUsernameChange(user);
        setShowUsernameChange(true);
        toast({
          title: "Username Change Required",
          description: user.message || "Your username has been flagged and must be changed.",
          variant: "destructive",
        });
        return;
      }

      // Force immediate refetch of auth data after successful login
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      queryClient.refetchQueries({ queryKey: ["/api/auth/user"] });

      // Start activity tracking for all sessions
      startTrackingUser();

      toast({
        title: "Login successful!",
        description: "Welcome back to CA Gun Exchange!",
      });
      onSuccess();
    },
    onError: (error: any) => {
      setError(error.message || "Login failed");
    },
  });

  const handlePasswordChanged = () => {
    setShowPasswordReset(false);
    setUserRequiringReset(null);

    // Login was successful, complete the authentication process
    if (userRequiringReset) {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      queryClient.refetchQueries({ queryKey: ["/api/auth/user"] });
      startTrackingUser();

      toast({
        title: "Login successful!",
        description: "Welcome back to CA Gun Exchange!",
      });
      onSuccess();
    }
  };

  const handleUsernameChanged = (updatedUser: any) => {
    setShowUsernameChange(false);
    setUserRequiringUsernameChange(null);

    // Username change was successful, complete the authentication process
    setUser(updatedUser);
    startTrackingUser();

    toast({
      title: "Login successful!",
      description: "Welcome back to CA Gun Exchange!",
    });
    onSuccess();
  };

  const onSubmit = (data: LoginData) => {
    setError("");
    loginMutation.mutate(data);
  };

  if (showPasswordReset && userRequiringReset) {
    return <ForcedPasswordChange user={userRequiringReset} onPasswordChanged={handlePasswordChanged} />;
  }

  // Show username change form if user requires username change
  if (showUsernameChange && userRequiringUsernameChange) {
    return (
      <UsernameChangeForm
        currentUsername={userRequiringUsernameChange.username}
        onSuccess={handleUsernameChanged}
      />
    );
  }

  return (
    <Card className="w-full max-w-md mx-auto">
      <CardHeader>
        <CardTitle>Sign In</CardTitle>
        <CardDescription>
          Welcome back to CA Gun Exchange
        </CardDescription>
      </CardHeader>
      <CardContent>
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
          {error && (
            <Alert variant="destructive">
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}

          <div className="space-y-2">
            <Label htmlFor="username">Username</Label>
            <Input
              id="username"
              type="text"
              {...form.register("username")}
              data-testid="input-login-username"
            />
            {form.formState.errors.username && (
              <p className="text-sm text-destructive">
                {form.formState.errors.username.message}
              </p>
            )}
          </div>

          <div className="space-y-2">
            <Label htmlFor="password">Password</Label>
            <div className="relative">
              <Input
                id="password"
                type={showPassword ? "text" : "password"}
                {...form.register("password")}
                className="pr-9"
                data-testid="input-login-password"
              />
              <Button
                type="button"
                variant="ghost"
                size="sm"
                className="absolute right-1 top-1 h-7 w-7 p-0"
                onClick={() => setShowPassword(!showPassword)}
                data-testid="button-toggle-login-password"
                tabIndex={-1}
              >
                {showPassword ? (
                  <EyeOff className="h-4 w-4" />
                ) : (
                  <Eye className="h-4 w-4" />
                )}
              </Button>
            </div>
            {form.formState.errors.password && (
              <p className="text-sm text-destructive">
                {form.formState.errors.password.message}
              </p>
            )}
          </div>

          <div className="flex items-center space-x-2">
            <Checkbox 
              id="rememberUsername" 
              {...form.register("rememberUsername")}
              data-testid="checkbox-remember-username"
            />
            <Label 
              htmlFor="rememberUsername" 
              className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70"
            >
              Remember my username
            </Label>
          </div>

          <Button
            type="submit"
            className="w-full bg-forum-accent hover:bg-forum-accent/90"
            disabled={loginMutation.isPending}
            data-testid="button-login-submit"
          >
            {loginMutation.isPending ? "Signing In..." : "Sign In"}
          </Button>
        </form>
      </CardContent>
    </Card>
  );
}

client/src/components/auth/register-form.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation } from "@tanstack/react-query";
import { insertUserSchema, type InsertUser } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Eye, EyeOff } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/lib/auth";

interface RegisterFormProps {
  onSuccess: () => void;
}

export default function RegisterForm({ onSuccess }: RegisterFormProps) {
  const { toast } = useToast();
  const { setUser } = useAuth();
  const [error, setError] = useState<string>("");
  const [showPassword, setShowPassword] = useState(false);

  const form = useForm<InsertUser>({
    resolver: zodResolver(insertUserSchema),
    defaultValues: {
      username: "",
      email: "",
      password: "",
      firstName: "",
      lastName: "",
      dateOfBirth: "",
    },
  });

  const registerMutation = useMutation({
    mutationFn: async (userData: InsertUser) => {
      const response = await apiRequest("/api/auth/register", { method: "POST", body: userData });
      return response;
    },
    onSuccess: (user) => {
      setUser(user);
      toast({
        title: "Registration successful!",
        description: "Welcome to CA Gun Exchange.",
      });
      onSuccess();
    },
    onError: (error: any) => {
      setError(error.message || "Registration failed");
    },
  });

  const onSubmit = (data: InsertUser) => {
    setError("");
    registerMutation.mutate(data);
  };

  return (
    <Card className="w-full max-w-md mx-auto">
      <CardHeader>
        <CardTitle>Create Account</CardTitle>
        <CardDescription>
          Join the CA Gun Exchange community. You must be 18 or older to register.
        </CardDescription>
      </CardHeader>
      <CardContent>
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
          {error && (
            <Alert variant="destructive">
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}

          <div className="space-y-2">
            <Label htmlFor="username">Username</Label>
            <Input
              id="username"
              type="text"
              {...form.register("username")}
              data-testid="input-username"
            />
            {form.formState.errors.username && (
              <p className="text-sm text-destructive">
                {form.formState.errors.username.message}
              </p>
            )}
          </div>

          <div className="space-y-2">
            <Label htmlFor="email">Email</Label>
            <Input
              id="email"
              type="email"
              {...form.register("email")}
              data-testid="input-email"
            />
            {form.formState.errors.email && (
              <p className="text-sm text-destructive">
                {form.formState.errors.email.message}
              </p>
            )}
          </div>

          <div className="grid grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="firstName">First Name</Label>
              <Input
                id="firstName"
                type="text"
                {...form.register("firstName")}
                data-testid="input-first-name"
              />
              {form.formState.errors.firstName && (
                <p className="text-sm text-destructive">
                  {form.formState.errors.firstName.message}
                </p>
              )}
            </div>

            <div className="space-y-2">
              <Label htmlFor="lastName">Last Name</Label>
              <Input
                id="lastName"
                type="text"
                {...form.register("lastName")}
                data-testid="input-last-name"
              />
              {form.formState.errors.lastName && (
                <p className="text-sm text-destructive">
                  {form.formState.errors.lastName.message}
                </p>
              )}
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="password">Password</Label>
            <div className="relative">
              <Input
                id="password"
                type={showPassword ? "text" : "password"}
                {...form.register("password")}
                className="pr-9"
                data-testid="input-password"
              />
              <Button
                type="button"
                variant="ghost"
                size="sm"
                className="absolute right-1 top-1 h-7 w-7 p-0"
                onClick={() => setShowPassword(!showPassword)}
                data-testid="button-toggle-register-password"
                tabIndex={-1}
              >
                {showPassword ? (
                  <EyeOff className="h-4 w-4" />
                ) : (
                  <Eye className="h-4 w-4" />
                )}
              </Button>
            </div>
            {form.formState.errors.password && (
              <p className="text-sm text-destructive">
                {form.formState.errors.password.message}
              </p>
            )}
          </div>

          <div className="space-y-2">
            <Label htmlFor="dateOfBirth">Date of Birth</Label>
            <Input
              id="dateOfBirth"
              type="date"
              {...form.register("dateOfBirth")}
              data-testid="input-date-of-birth"
            />
            {form.formState.errors.dateOfBirth && (
              <p className="text-sm text-destructive">
                {form.formState.errors.dateOfBirth.message}
              </p>
            )}
            <p className="text-xs text-gray-500">
              You must be 18 or older to register
            </p>
          </div>

          <div className="bg-forum-warning/10 border border-forum-warning/20 rounded-lg p-3">
            <p className="text-xs text-forum-warning font-medium">
              By registering, you acknowledge that you are 18 years or older and agree to comply with all federal, state, and local laws regarding firearm transactions.
            </p>
          </div>

          <Button
            type="submit"
            className="w-full bg-forum-accent hover:bg-forum-accent/90"
            disabled={registerMutation.isPending}
            data-testid="button-register-submit"
          >
            {registerMutation.isPending ? "Creating Account..." : "Create Account"}
          </Button>
        </form>
      </CardContent>
    </Card>
  );
}

client/src/components/auth/username-chan.content:
import React, { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { AlertCircle, User } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { useLocation } from "wouter";

interface UsernameChangeFormProps {
  currentUsername: string;
  onSuccess: (newUser: any) => void;
}

export default function UsernameChangeForm({ currentUsername, onSuccess }: UsernameChangeFormProps) {
  const [newUsername, setNewUsername] = useState("");
  const { toast } = useToast();
  const [, navigate] = useLocation();

  const changeUsernameMutation = useMutation({
    mutationFn: async (username: string) => {
      return await apiRequest("/api/profile/change-username", {
        method: "PUT",
        body: { newUsername: username }
      });
    },
    onSuccess: (data: any) => {
      toast({ 
        title: "Username Changed Successfully",
        description: "Your username has been updated and you can now continue using the platform."
      });
      onSuccess(data);
      navigate("/");
    },
    onError: (error: Error) => {
      toast({
        title: "Error Changing Username",
        description: error.message || "Failed to change username. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newUsername.trim()) {
      toast({
        title: "Username Required",
        description: "Please enter a new username.",
        variant: "destructive",
      });
      return;
    }
    changeUsernameMutation.mutate(newUsername.trim());
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-orange-50 to-red-100 dark:from-gray-900 dark:to-gray-800">
      <Card className="w-full max-w-md shadow-lg">
        <CardHeader className="text-center">
          <div className="flex justify-center mb-4">
            <div className="p-3 bg-orange-500/10 rounded-full">
              <AlertCircle className="h-8 w-8 text-orange-500" />
            </div>
          </div>
          <CardTitle className="text-2xl font-bold">Username Change Required</CardTitle>
          <CardDescription>
            Your current username has been flagged and must be changed before you can continue.
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div className="p-4 bg-orange-50 dark:bg-orange-900/20 rounded-lg border border-orange-200 dark:border-orange-800">
              <div className="flex items-center space-x-2 text-orange-800 dark:text-orange-200">
                <User className="h-4 w-4" />
                <span className="text-sm font-medium">Current Username: {currentUsername}</span>
              </div>
              <p className="text-sm text-orange-700 dark:text-orange-300 mt-2">
                Your username has been flagged by an administrator and must be changed to continue using CA Gun Exchange.
              </p>
            </div>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="newUsername">New Username</Label>
                <Input
                  id="newUsername"
                  type="text"
                  placeholder="Enter your new username"
                  value={newUsername}
                  onChange={(e) => setNewUsername(e.target.value)}
                  required
                  data-testid="input-new-username"
                />
              </div>

              <Button
                type="submit"
                className="w-full"
                disabled={changeUsernameMutation.isPending}
                data-testid="button-change-username"
              >
                {changeUsernameMutation.isPending ? "Changing Username..." : "Change Username"}
              </Button>
            </form>

            <div className="text-center text-sm text-muted-foreground">
              <p>You must change your username to continue using the platform.</p>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

